# Select image file
def open_file():
   file = filedialog.askopenfile(mode='r', filetypes=[('Image File', '*.png')])
   if file:
      filepath = os.path.abspath(file.name)
      Label(window, text="The File is located at : " + str(filepath), font=('Aerial 11')).pack()

